import react from "react";
import { BrowserRouter as Router,Route,Switch} from 'react-router-dom';

function projects(){
   const Box = (props)=>{
    return(
      <>
          <div className="top py-[2vh] px-[5vw] text-left md:text-right md:py-[5vh]">
            <p className="font-Libre font-bold text-[#7b7461] text-lg">{props.field}</p>
            <p className="font-Libre text-[#333333] text-2xl pt-1 md:text-3xl">{props.title}</p>
          </div>
      </>
    )
   }

    return(
      <div className="Container w-screen h-full px-[10vw]">
        <div className="box mt-[5vh] cursor-pointer w-full h-[30rem]  rounded-2xl 
         bg-[conic-gradient(at_bottom_right,_var(--tw-gradient-stops))]
         from-indigo-200 via-red-200 to-yellow-100 hover:shadow-xl duration-500 hover:-translate-y-5 md:h-[50rrem]">
          <Box field="Game Dev" title="The Pandemic" />
         </div>

         <div className="box mt-[5vh] cursor-pointer w-full h-[30rem]  rounded-2xl 
          bg-gradient-to-bl from-yellow-200 via-pink-200 to-pink-400
          hover:shadow-xl duration-500 hover:-translate-y-5 md:h-[50rrem]">
          <Box field="Website" title="FarmHub" />
         </div>
         <div className="box mt-[5vh] cursor-pointer w-full h-[30rem]  rounded-2xl 
       bg-gradient-to-bl from-rose-100 to-teal-100
         hover:shadow-xl duration-500 hover:-translate-y-5 md:h-[50rrem]">
          <Box field="Game Dev - Mobile" title="Endless" />
         </div>
      </div>
    );
}

export default projects;
